void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 buf = texelFetch(iChannel0, ivec2(fragCoord), 0);

    vec3 c = buf.rgb;

    c = sqrt(c);

    fragColor = vec4(c, 1.);
}

vec3 hsv2rgb_smooth( in vec3 c )
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );

	rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing	

	return c.z * mix( vec3(1.0), rgb, c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord -iResolution.xy*.5)/iResolution.y*2.;
    vec2 m = (iMouse.xy -iResolution.xy*.5)/iResolution.y*2.;

    if(iMouse.z < 0.5)
    {
        m = vec2(cos(iTime+1.)*1.5, sin(iTime+1.)*.25+.2);
    }

    vec3 col = vec3(0.);

    Circle c = Circle(vec2(0.), 1.);

    int N = 100;
    for(int kk=0; kk<N; kk++)
    {
        float k = float(kk)/float(N);
        vec2 p0 = getPt(c, k);
        vec2 n0 = -p0;
        vec2 d0 = reflect(normalize(p0-m), -p0);

        vec2 p1 = getPt(c, k+1./float(N));
        vec2 d1 = reflect(normalize(p1-m), -p1);

        vec2 t;
        intersect(Ray(p0, d0), Ray(p1, d1), t);

        vec2 pp = p0+d0*t.x;

        float d = dist(HalfPlane2(pp, d0, d1),p);

        vec3 cc = hsv2rgb_smooth(vec3(k,1.,1.));

        float dd0 = dist(Ray(p0, d0), p);
        float dd1 = dist(Ray(p1, d1), p);

        cc = mix(hsv2rgb_smooth(vec3(k,1.,1.)), hsv2rgb_smooth(vec3(k+1./float(N),1.,1.)), dd0/(dd1+dd0));

        float ddd0 = dist(p, pp);

        vec2 p2 = getPt(c, k+2./float(N));
        vec2 d2 = reflect(normalize(p2-m), -p2);
        intersect(Ray(p1, d1), Ray(p2, d2), t);
        pp = p1+d1*t.x;
        float ddd1 = dist(p, pp);

        float fac =  1./mix(ddd0, ddd1, dd0/(dd1+dd0));


        float alpha = mix(1.,0.,clamp(d*iResolution.y*1.-.0, -1., 1.)*.5+.5);
        col += cc*fac*alpha;
    }
    fragColor = vec4(col*1e-1, 1.);
}

#define PI	3.14159265359


struct Circle
{
    vec2 ctr;
    float rad;
};

struct Ray
{
    vec2 org;
    vec2 dir;
};

struct Segment
{
    vec2 A;
    vec2 B;
};

struct Bezier3
{
    vec2 A;
    vec2 B;
    vec2 C;
};

struct Bezier4
{
    vec2 A;
    vec2 B;
    vec2 C;
    vec2 D;
};

struct Grid
{
    float inter;
};

bool intersect(Circle c, Ray r, out vec2 t)
{
    float A = dot(r.dir, r.dir);
    float B = dot(r.dir, -c.ctr+r.org);
    float C = dot(c.ctr, c.ctr) +dot(r.org, r.org) -2.*dot(c.ctr, r.org) - c.rad*c.rad;

    float delta = B*B-A*C;

    if(delta < 0.0)
    	return false;

    t = (vec2(-B) + vec2(sqrt(delta))*vec2(-1.,1.))/A;
    return true;
}

bool intersect(Ray r0, Ray r1, out vec2 t)
{
    t = inverse(mat2(r0.dir, -r1.dir))*(r1.org-r0.org);
    return true;
}

vec2 getPt(Ray r, float t)
{
    return r.org + t*r.dir;
}

vec2 getPt(Circle c, float t)
{
    return c.ctr + c.rad*vec2(cos(t*PI*2.), sin(t*PI*2.));
}

vec2 getPt(Segment c, float t)
{
    return mix(c.A, c.B, t);
}

vec2 getPt(Bezier3 c, float t)
{
    return mix(mix(c.A, c.B, t), mix(c.B, c.C, t), t);
}

vec2 getPt(Bezier4 c, float t)
{
    return mix(mix(mix(c.A, c.B, t), mix(c.B, c.C, t), t), mix(mix(c.B, c.C, t), mix(c.C, c.D, t), t), t);
}

float dist(Ray r, vec2 p)
{
    return abs(dot(r.dir.yx*vec2(-1., 1.), p-r.org)/dot(r.dir, r.dir));


    float t = dot(r.dir, p-r.org);
    t = max(t, 0.);
    return length(getPt(r, t) - p);
}

float dist(vec2 p0, vec2 p1)
{
    return length(p1-p0);
}

float dist(Circle c, vec2 p)
{
    return abs(length(c.ctr-p)-c.rad);
}

float dist(Segment s, vec2 p)
{
	vec2 pa = p-s.A, ba = s.B-s.A;
	float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
	return length( pa - ba*h );

}

float dist(Grid g, vec2 p)
{
    vec2 d = mod(p,g.inter);
    d = min(d, g.inter-d);
    return min(d.x, d.y);

    //bvec2 l = greaterThan(mod(p,g.inter*2.),vec2(g.inter));
    //return all(l)||all(not(l)) ? 1. : 0.;
}

struct HalfPlane
{
    vec2 org;
    vec2 dir;
};

struct HalfPlane2
{
    vec2 org;
    vec2 dir0;
    vec2 dir1;
};

float dist(HalfPlane r, vec2 p)
{
    return (dot(r.dir, p-r.org)/dot(r.dir, r.dir));
}

float dist(HalfPlane2 r, vec2 p)
{
    float d0 = dist(HalfPlane(r.org, r.dir0.yx*vec2(-1.,1.)), p);
    float d1 = dist(HalfPlane(r.org, r.dir1.yx*vec2(-1.,1.)), p);

    /*
    if(dot(r.dir0+r.dir1, p-r.org)<0.)
        return dist(r.org, p);
	//*/

    return max(min(d0, d1), min(-d0, -d1));
}

// Solve cubic equation for roots
vec3 solveCubic(float a, float b, float c)
{
    float p = b - a*a / 3.0, p3 = p*p*p;
    float q = a * (2.0*a*a - 9.0*b) / 27.0 + c;
    float d = q*q + 4.0*p3 / 27.0;
    float offset = -a / 3.0;
    if(d >= 0.0) { 
        float z = sqrt(d);
        vec2 x = (vec2(z, -z) - q) / 2.0;
        vec2 uv = sign(x)*pow(abs(x), vec2(1.0/3.0));
        return vec3(offset + uv.x + uv.y);
    }
    float v = acos(-sqrt(-27.0 / p3) * q / 2.0) / 3.0;
    float m = cos(v), n = sin(v)*1.732050808;
    return vec3(m + m, -n - m, n - m) * sqrt(-p / 3.0) + offset;
}

float dist(Bezier3 B, vec2 p)
{
    vec2 a = B.B - B.A, b = B.A - B.B * 2.0 + B.C, c = a * 2.0, d = B.A - p;
    vec3 k = vec3(3.*dot(a,b),2.*dot(a,a)+dot(d,b),dot(d,a)) / dot(b,b);      
    vec3 t = clamp(solveCubic(k.x, k.y, k.z), 0.0, 1.0);
    vec2 pos = B.A + (c + b*t.x)*t.x;
    float dis = length(pos - p);
    pos = B.A + (c + b*t.y)*t.y;
    dis = min(dis, length(pos - p));
    pos = B.A + (c + b*t.z)*t.z;
    dis = min(dis, length(pos - p));
    return dis;
}

float dist(Bezier4 BB, vec2 p)
{
    vec2 A = BB.A, B = BB.D;

    float ppt;
    float At = .0;
    float Bt = 1.;

    vec2 pp;

    float dis = dist(pp, p);

    for(int i=0; i<5; ++i)
    {
        ppt = (At+Bt)*.5;
        pp = getPt(BB, ppt);

        if(dist(Segment(A, pp), p) < dist(Segment(pp, B), p))
        {
            Bt = ppt;
        	B = getPt(BB, Bt);
        }
        else
        {
            At = ppt;
        	A = getPt(BB, At);
        }

    }

    return min(dist(Segment(A, pp), p), dist(Segment(B, pp), p));
}


#define DRAW(O,P,C,W, CC)	CC=mix(CC,C,mix(1.,0.,clamp(dist(O,P)*iResolution.y/2. -  W, -1., 1.)*.5+.5))
