float	de(vec3 p, inout vec3 b)
{
	float	mind = 1e5;
    rotate(p.xy, t*.1*0.+p.z*.01251*1.+( cos(t*1.+floor(p.z*1.)*.125 )*.25+1.*sin(t+p.z*.1)/*+(p.z)*/ ));
    mind = min(mind, mylength(abs(sin((p.z/max(.00001, max(abs(p.x), abs(p.y) )))*.125 )))+1.5);
    mind = min(mind, length(abs(p.xy)-vec2(15.) )+3. );

	return (mind);
}

float	de_4(vec3 p)
{
	float	mind = 1e5;
	vec3	pr = p;

	vec2	q;
	q = vec2(length(pr.yx) - 4., pr.z );
    q.y = rot(q.xy, vec2(-1., 0.)).x;

	mind = length(q) - 3.5;

	return mind;
}

float	de_2(vec3 p, inout vec3 b)
{
    return (de(p, b)-de_4(p)*.125);
}

float	scene(vec3 p, inout vec3 b)
{
    vec2	rot = vec2( cos(t/2.), sin(t/2.) );//p.z += sin(t*10.)*4.;
	float	mind = 1e5;

	mind = de_2(p, b);

    return(mind);
}

vec4	march(vec3 pos, vec3 dir, inout vec3 b)
{
    vec2	dist = vec2(0.0, 0.0);
    vec3	p = vec3(0.0, 0.0, 0.0);
    vec4	step = vec4(0.0, 0.0, 0.0, 0.0);
	vec3	dirr;

    for (int i = -1; i < I_MAX; ++i)
    {
        dirr = dir;
    	p = pos + dirr * dist.y;
        dist.x = scene(p, b);
        dist.y += dist.x;
        if (dist.x < E )
        {
            step.y = 1.;
            break;
        }
        step.x++;
    }
    step.w = dist.y;
    return (step);
}

// Utilities

vec3	camera(vec2 uv)
{
    float		fov = 1.;
	vec3		forw  = vec3(0.0, 0.0, -1.0);
	vec3    	right = vec3(1.0, 0.0, 0.0);
	vec3    	up    = vec3(0.0, 1.0, 0.0);

    return (normalize((uv.x-.5) * right + (uv.y-0.5) * up + fov * forw));
}

vec2	rot(vec2 p, vec2 ang)
{
	float	c = cos(ang.x);
    float	s = sin(ang.y);
    mat2	m = mat2(c, -s, s, c);

    return (p * m);
}

void rotate(inout vec2 v, float angle)
{
	v = vec2(cos(angle)*v.x+sin(angle)*v.y,-sin(angle)*v.x+cos(angle)*v.y);
}

float	mylength(float p)
{
	float	ret = 0.;
    p = p*p*p*p;
    ret = p;
    ret = pow(ret, 1./3.);
    return (ret);
}

float	mylength(vec2 p)
{
	float	ret = 0.;
    p = p*p*p*p;
    ret = p.x+p.y;
    ret = pow(ret, 1./4.);
    return (ret);
}

float	mylength(vec3 p)
{
	float	ret = 0.;
    p = p*p*p*p;
    ret = p.x+p.y+p.z;
    ret = pow(ret, 1./4.);
    return (ret);
}
