void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float size1 = 180.0;
    float size2 = 60.0;

    float offsetX = iMouse.x;
    float offsetY = iMouse.y;

    if(mod(fragCoord.x - offsetX, size1) > size1 - 2.0 || mod(fragCoord.y - offsetY, size1) > size1 - 2.0) {
    	fragColor = vec4(0.5,0.5,0.5,1);
    } else if(mod(fragCoord.x - offsetX, size2) > size2 - 2.0 || mod(fragCoord.y - offsetY, size2) > size2 - 2.0) {
    	fragColor = vec4(0.8,0.8,0.8,1);
    } else {
    	fragColor = vec4(1,1,1,1);
    }

}
