float pivot(in vec2 uv,in float y)
{
    const float tickness = 0.02;
    return smoothstep(uv.y - tickness, uv.y, y) -
           smoothstep(uv.y, uv.y + tickness, y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;    

    float y = step(0.5, uv.x);

    const vec3 green = vec3(0.01, 0.99, 0.01);
    const vec3 white = vec3(0.99, 0.99, 0.99);

    float pct = pivot(uv, y);
    vec3 color = pct * green + (1.0 - pct) * y * white;

    fragColor = vec4(color,1.0);
}
