void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float PI = 3.1415;
    float radius = 120.0;
    vec2 midP = vec2(iResolution.x/2.0, iResolution.y/2.0);
    float mid_r = 700.0;
    float ball_r = 300.0;
    float freq = 0.6;

    float influence = 0.0;
    for (float i = 0.0; i < 8.0; i++) {
        float phase = i * PI / 4.0;
        float theta = i * PI / 4.0;
        float offset = radius * (sin(PI * freq * iTime + phase) * 0.5 + 0.5);
        float offset_x = offset * cos(theta);
        float offset_y = offset * sin(theta);
        vec2 ball = vec2(midP.x + offset_x, midP.y + offset_y);

        influence = influence + ball_r/(pow((fragCoord.x-ball.x),2.0) + pow((fragCoord.y-ball.y),2.0));
    }

    influence = influence + mid_r/(pow((fragCoord.x-midP.x),2.0) + pow((fragCoord.y-midP.y),2.0));

    if (influence > 1.0) {
        fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    } else {
        fragColor = vec4(1.0, 1.0, 1.0, 1.0);
    }



}
