const vec4 col0 = vec4(0.5, 0.5, 0.5, 1.0);
const vec4 col1 = vec4(0.7, 0.7, 0.7, 1.0);
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    mat2 rotate = mat2(0.7071, -0.7071,
                       0.7071, 0.7071);
    fragCoord = rotate * fragCoord;
    float d = fragCoord.x - fragCoord.y;
    if( mod(d,32.0) > 16.0){
        fragColor  = col0;
    }else{
        fragColor = col1;
    }
}
