void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = vec4(-0.1, 0.1, 0.2, 1.0) * 1.2;

    vec4 mm = texture(iChannel2, uv);

    vec4 water = vec4(0.03, 1.2, 0.3, 1.0);
    vec4 light = vec4(0.0, 1.2 * mm.x*1.1, 0.5 * pow(mm.x,1.4), 1.0);

    float wave = step(sin(uv.y * 180.0 * texture(iChannel2, uv).x * mod(iTime, 1.0) * texture(iChannel0, uv).x), 0.0);
    wave *= 1.0 - uv.y * mix(texture(iChannel0, vec2(uv.x*mod(iTime,1.0), uv.x)).x, 1.0, 1.5);

    fragColor = mix(fragColor, water, wave * (1.0 - uv.y));
    fragColor = mix(fragColor, light, 2.0 * mm.x * sin(iTime * 1.04));
    fragColor = mix(fragColor, vec4(0.0,1.0,1.0,1.0), uv.y * 0.2243 * mm.x * sin(iTime * 1.0 * mm.x));
    fragColor.y -= fragColor.z * wave * sin(iTime * 0.1);
    fragColor += fragColor.y * mm.x * uv.y * 1.02;
}
