float tw(float t){ //triangle wave

		return abs	(
	    		   	mix(	-1.0,	1.0, fract(t)	)
			  		)
			;}


	void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = 2.0*fragCoord.xy / iResolution.xy - vec2(1.0);

	float val= tw(uv.x*12.0*tw(uv.y*0.5)+  iTime);

	fragColor = vec4(abs(uv),val,1.0);
}
