void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
	float width = .01;
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec4 pixel = texture(iChannel0, vec2(uv.x, uv.y));
	vec4 right = texture(iChannel0, vec2(uv.x+width, uv.y));
	vec4 up = texture(iChannel0, vec2(uv.x, uv.y+width));
	float pixelSum = pixel.r+pixel.g+pixel.b;
	float rightSum = right.r+right.g+right.b;
	float upSum = up.r+up.g+up.b;
	float edge = abs(pixelSum-rightSum)+abs(pixelSum-upSum);

	//vec4 noise = texture(iChannel2,uv);

	edge = clamp(edge,0.,1.);

	float blackboard = texture(iChannel1,uv).r;


	float color = max(edge,0.)*(blackboard*.3)+blackboard*.3;
	fragColor = vec4(color);
}
