void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

	//current spectrum value 
	float sp = texture(iChannel0, vec2(uv.x, 0.0)).r; // r=g=b, a=1.0

	float r = sp;
	float g = sp * uv.y;

	float smoothdist = 0.05;

	float dist = uv.y - sp;
	fragColor = vec4(vec3(r, g, 0.0) * ((dist < 0.0) ? 1.0 : (dist > smoothdist) ? 0.0 : ((smoothdist - dist) / smoothdist)), 1.0);
}
