#define COEFFICIENT vec2(-0.805, 0.156)

#define MULT(a, b)	(vec2((a.x) * (b.x) - (a.y) * (b.y),	(a.y) * (b.x) + (a.x) * (b.y)))
#define ADD(a, b)	(vec2((a.x) + (b.x),					(a.y) + (b.y)))
#define LENGTH(a)	(sqrt((a.x) * (a.x) + (a.y) * (a.y)))


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float threshold = 2.0 * (sin(iTime) + 1.0);

	vec2 mouse = iMouse.xy / iResolution.xy;

	vec2 z = fragCoord.xy / iResolution.xy * 3.0 - 3.0 + (mouse * 4.0);

	int i;
	float l = LENGTH(z);
	for (int k = 1; k < 200; k++) {

		if (l > threshold)
			break;

		z = ADD(MULT(z, z), COEFFICIENT);
		l = LENGTH(z);

		i = k;
	}

	if (i > 198) {
		vec2 uv = fragCoord.xy / iResolution.xy;
		fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);
	} else
		fragColor = vec4(float(i) / 10.0, 0.0, 1.0, 1.0);
}
