// adapated from info here: https://www.cs.drexel.edu/~david/Classes/Talks/RezaPres.pdf

#define DEBUG_UNDER_OVER_FLOW 0

float GetC(int i)
{
	return pow(0.707, float(i));
}

float GetF(int i)
{
	return pow(2.0, float(i));
}

float GetP(float axis, int i)
{
	return 1.57 * sin(0.5*GetF(i)*axis);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	// input parameters
	const float scale = 0.75;
	const float timeMultiplier = 0.125;
	const float speedX = 1.0;
	const float speedY = 2.0;
	const int numLoops = 6;
	const float T0 = 0.0;
	const float K = 0.4;

	// scale it and make it square
	vec2 uv = scale * fragCoord.xy / iResolution.xy;
	uv.x *= iResolution.x / iResolution.y;

	// advance time
	uv.x += iTime * timeMultiplier * speedX;
	uv.y += iTime * timeMultiplier * speedY;

	float color = 0.0;
	for (int i = 0; i < numLoops; ++i)
	{
		float thisY = GetC(i) * sin(GetF(i)*uv.y + GetP(uv.x,i)) + T0;

		float thisX = 0.0;
		for (int j = 0; j < numLoops; ++j)
		{
			if (j >= i)
			{
				thisX += GetC(j) * sin(GetF(j)*uv.x + GetP(uv.y,j)) + T0;
			}
		}
		color += thisX * thisY;
	}
	color *= K;

	float red = 0.0;
	float green = 0.0;
	float blue = 0.0;

	#if DEBUG_UNDER_OVER_FLOW
	if (color > 1.0)
	{
		red = 1.0;
	}
	else if (color < 0.0)
	{
		blue = 1.0;
	}
	else
	#endif
	{
		red = color;
		green = color;
		blue = color;
	}

	fragColor = vec4(red,green,blue,1.0);
}
