//pc = pixel coordinates
//c = costant proper of Julia Set

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 pc = 1.2*(-1.0 + 2.0*fragCoord.xy/iResolution.xy)*vec2(iResolution.x/iResolution.y, 1.0);
	vec2 c = 0.7*cos(vec2(0.0,1.5) + 0.15*iTime) - 0.3*cos(vec2(0.0,1.5) + 0.25*iTime);

	float f = 1e20;
	for(int i = 0; i < 256; i++) {
		//The Julia fractal equation: pc = pc*pc + c
		pc = vec2(pc.x*pc.x-pc.y*pc.y, 2.0*pc.x*pc.y) + c;
		//Trap orbit from iq site
		f = min(f, dot(pc, pc));
	}

	if(iMouse.z > 0.0) {
		f = log(f)/8.0;
	} else {
		f = 1.0+log(f)/16.0;
	}

	fragColor = mix(vec4(0.0, 0.0, 0.0, 1.0), vec4(f,f*f*2.0,f*f*f*0.6, 1.0), 0.4);
}
