#define PI 3.1415926535897932384626433832795

vec3 hsv2rgb(in vec3 c) {
    vec3 rgb = clamp(abs(mod(c.x * 6.0 + vec3(0.0, 4.0, 2.0), 6.0) - 3.0) - 1.0, 0.0, 1.0);
	rgb = rgb * rgb * (3.0 - 2.0 * rgb);
	return c.z * mix(vec3(1.0), rgb, c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = fragCoord.xy / 100.;
    vec2 rv = iResolution.xy / 200.;
	vec2 diff = rv - uv;
    float angle = atan(diff.x, diff.y);
    float angleparts = (2. * PI + angle) / (2. * PI);// Angle translated to 0..1
    float value = sin(angleparts * pow(min(iTime, 25.), 3.));
	fragColor = vec4(hsv2rgb(vec3(1.0, value, 1./distance(uv, rv))), 1.0);
}
