void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 center;
	center = iResolution.xy/2.0;

	float distan = distance(center,fragCoord.xy);
	float factor = ((distan/center[0])+(distan/center[1]));	

	float offset = sin(iTime);
	float final = 1.0-(offset+factor)/1.2;

	fragColor = vec4(0.0,final,0.0,1.0);

}
