#define T(i,j) texture(iChannel0,U/iResolution.xy+vec2(i,j)/iChannelResolution[0].xy)

void mainImage( out vec4 O, vec2 U ) 
{
    O = (   T(-1,-1)+T(0,-1)+T(1,-1)
          + T(-1, 0)+T(0, 0)+T(1, 0)
          + T(-1, 1)+T(0, 1)+T(1, 1) ) / 9.;

    float v = sin(6.28*3.*length(O.xyz));

    O *= 1.-smoothstep(0.,1., .5*abs(v)/fwidth(v));
}
