void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float scaledTime = iTime / 10.0;
    vec4 color = vec4(cos(scaledTime + 10.0), cos(scaledTime + 20.0), cos(scaledTime + 30.0), 1.0);
    float height = sin(fragCoord.x / cos(iTime / 50.0)) + 1.0;
    if(height > fragCoord.y / iResolution.y * 2.0)
    {
        //color = height / 2.0;
        color = vec4(cos(scaledTime), cos(scaledTime + 10.0), cos(scaledTime + 20.0), 1.0);
    }
	fragColor = color;
}
