void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = fragCoord.xy / iResolution.xy;

    float dx = abs(uv.x - 0.5);
    float dy = abs(uv.y - 0.5);
    float dist = sqrt(dx * dx + dy * dy);
    float angle = atan(uv.x - 0.5, uv.y - 0.5);
    angle += iTime * 0.1;

    float h = 1.0 - dist;
    fragColor = vec4(h, h, h, 1.0);

    float numTips = 5.0;
    float tipRadius = sin(iTime * 4.0) * 0.1;

    float desiredRadius = 0.4;
    desiredRadius += cos(angle * 5.0) * sin(iTime * 4.0) * 0.03;
    desiredRadius += sin(angle * 3.0) * sin(iTime * 3.0) * 0.05;
    desiredRadius += cos(angle * 7.0) * sin(iTime * 1.3) * 0.05;
    desiredRadius += sin(angle * 13.0) * sin(iTime * 0.5) * 0.03;

    float thickness = 0.01;
    if (dist > desiredRadius) {
        // Outside
        fragColor = vec4(1.0, 1.0, 1.0, 1.0);
    } else if (dist > desiredRadius - thickness) {
        // Outline
        fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    } else {
        // Inside
        fragColor = vec4(1.0, 1.0, 1.0, 1.0);
    }
}
