void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    if(uv.x < 0.25)
    {
    	fragColor.r = texture(iChannel0,vec2(0.4,0.0)).r;
    }
    else if(uv.x < 0.75)
    {
    	fragColor.g = texture(iChannel0,vec2(0.6,0.0)).r;
    }
	else 
    {
    	fragColor.b = texture(iChannel0,vec2(0.8,0.0)).r;
    }

    uv.x = uv.x * 1.5 - 0.25;

    fragColor = fragColor + texture(iChannel1, uv);

    uv.x *= 0.7;

    fragColor = fragColor - texture(iChannel2, uv);

	fragColor += vec4(uv,0.5+0.5*sin(iTime),1.0);
}

vec2 mainSound( in int samp, float time )
{
    return vec2( sin(6.2831*440.0*time)*exp(-3.0*time) );
}
