void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // 71.5bpm
    float bpm = 1.0 / 60.0 * 71.5;

    float t = iTime;
    float b = t * bpm * 3.1415926535;

	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 nuv = uv * 2.0 - 1.0;

    float m0 = texture(iChannel0, vec2(uv.x, 0.25)).x;
    float m1 = texture(iChannel0, vec2(uv.x, 0.75)).x;

    float c0 = pow(m0, 8.0) * uv.y;
    float c1 = m1 * uv.y * 0.0;
    float c2 = m1 * pow(length(1.0 - abs(nuv * vec2(1.0, 4.0 * sin(b + bpm * 1.25)))), 1.5);

    c1 = abs(sin(b * 2.0)) * length(nuv) * 1.0;

    float drum0 = texture(iChannel0, vec2(1.0, 0.25)).x;
    float drum1 = texture(iChannel0, vec2(1.0, 0.25)).x;
    float drum2 = texture(iChannel0, vec2(1.0, 0.25)).x;
	float drum = pow((drum0 + drum1 + drum2) / 3.0, 2.0);

    drum = smoothstep(0.0, 1.0, drum * 2.0);

    float cd = drum;

    vec3 col0 = vec3(0.14, 0.29, 0.45);
    vec3 col1 = vec3(0.26, 0.65, 0.22);
    vec3 col2 = vec3(0.76, 0.12, 1.22);

    fragColor.xyz = col0 + col0 * c1;
    fragColor.xyz += col1 * c2 * 0.1;
    fragColor.xyz += col2 * drum;

    fragColor.xyz -= pow(length(nuv), 3.0) * 0.5 * c2 * c1;
    fragColor.xyz += pow(m1, 12.0) * pow(abs(nuv.y), 12.0) * 80.0;
}
