void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 nuv = uv * 2.0 - 1.0;

    float bpm = 1.0 / 60.0 * 180.0;
    float t = iTime;
	float bt = t * bpm;
    float pi = 3.14159265359;
    float btp = bt * pi;

    vec3 col0 = vec3(0.51, 0.57, 0.48);
    vec3 col1 = vec3(0.41, 0.42, 0.33);
	vec3 col2 = vec3(0.04, 0.23, 0.15);
    vec3 col3 = vec3(0.35, 0.25, 0.16);
    vec3 col4 = vec3(0.82, 0.77, 0.64);

    vec3 c = vec3(0.0);

    vec2 ms = vec2(1.0 / 32.0, 0.25);
    float m0 = texture(iChannel0, ms * 0.0).x;
    float m1 = texture(iChannel0, ms * 5.0).x;
    float m2 = texture(iChannel0, ms * 28.0).x;

    vec4 t0 = texture(iChannel1, uv);
    vec4 t0a = texture(iChannel1, uv + vec2(btp * 0.002, 0.0));
    vec4 t1 = texture(iChannel2, uv);
    vec4 t1a = texture(iChannel2, nuv * 1.0 * t0.x + vec2(btp * 0.002 + m1 * 0.03, 0.0));

    m0 = pow(m0, 3.0);
    m2 = pow(m2 * abs(nuv.x), 1.5);

    float f0 = 1.0;
    float f1 = max(0.0, sin(uv.y * pi * 20.0 + t0.x * 20.0 * pow(1.0 - uv.y - 0.2, 3.0) * sin(btp * length(nuv) * 0.005 + t)));
    float f2 = max(0.0, sin(length(nuv * 200.0 * t1a.x * 0.01)));
    float f3 = max(0.0, sin(t0a.x * (abs(sin(btp * 0.01)) + 3.0 + 2.0 * m1 * uv.y) * 0.2) * max(0.0, pow(uv.y, 4.5)));
	float f4 = max(0.0, min(sin(uv.x * 32.0 + btp * 2.0), cos(uv.y * 16.0 + btp * -0.05 * uv.x * m0)));

    c = mix(col0, col1, (1.0 - (f0 - f1)) * 1.0 * pow(max(0.0, 1.0 - nuv.y - 0.5), 2.0));
    c = mix(c, col2, smoothstep(0.0, 1.0, f2 * 1.0 * (1.0 - uv.y - 0.1)));
    c = mix(c, col4, f3 * 1.0);
    c = mix(c, col3, f4 * 1.0 * m2);

    //c += col0;
    //c += col1 * max(0.0, sin(uv.y * pi * 20.0 + t0.x * sin(btp * length(nuv) * 0.005 + t)));

    fragColor.xyz = c;

    //fragColor.xyz = pow(abs(sin(bt * pi - pi)), 4.0) * col3;
}
