#define M_PI 3.1415926535897932384626433832795

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float offset = iMouse.x / iResolution.x;//sin(iTime);
    float zoom = 1.0 + (iMouse.y / iResolution.y);

	vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= 0.5;

    vec2 polar;
    polar.x = atan(uv.x ,uv.y) / (M_PI * 2.0) + offset;
    polar.y = sqrt((uv.x*uv.x)+(uv.y*uv.y)) / sqrt(0.5) * 1.0/zoom;
    fragColor = texture(iChannel0, polar);
}
