void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel0, uv).rgba;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 position = fragCoord.xy / iResolution.xy;
	vec2 mouse = iMouse.xy / iResolution.xy;
    vec3 color = vec3(0.0);
	color.r += (cos(iTime * 20.0) * 0.5 + 0.5) * (1.0 - smoothstep(0.0, 0.01, distance(position, mouse)));
	vec3 pixel = vec3(1.0 / iResolution.xy, 0.0);
	float a = texture(iChannel0, position + pixel.xz).r * 0.25;
	float b = texture(iChannel0, position - pixel.xz).r * 0.25;
	float c = texture(iChannel0, position + pixel.zy).r * 0.25;
	float d = texture(iChannel0, position - pixel.zy).r * 0.25;
	color -= (a+b+c+d) * 0.25 * 0.02;
	vec2 dfx = vec2(a-b, c-d);
	color.r += texture(iChannel0, position + pixel.xy * dfx * 7.0).r; 
	color = clamp(color.rrr, 0.0, 1.0);
	color = (ceil (color * 255.0) / 255.0 + floor (color * 255.0) / 255.0) * 0.5;
    fragColor = color.rrrr;
}
