#define stepH 1.
#define stepV 1.

float rgbToGray(vec4 rgba) {
	const vec3 W = vec3(0.2125, 0.7154, 0.0721);
    return dot(rgba.xyz, W);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float posterSteps = 8.;

    // current location & color
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 curColor = texture(iChannel0, uv);

  	// get samples around pixel
	float colors[9];
    float stepX = stepH/iResolution.x;
    float stepY = stepV/iResolution.y;
    colors[0] = rgbToGray(texture(iChannel0, uv + vec2(-stepX, stepY)));
	colors[1] = rgbToGray(texture(iChannel0, uv + vec2(0, stepY)));
	colors[2] = rgbToGray(texture(iChannel0, uv + vec2(stepX, stepY)));
	colors[3] = rgbToGray(texture(iChannel0, uv + vec2(-stepX, 0)));
	colors[4] = rgbToGray(texture(iChannel0, uv));
	colors[5] = rgbToGray(texture(iChannel0, uv + vec2(stepX, 0)));
	colors[6] = rgbToGray(texture(iChannel0, uv + vec2(-stepX, -stepY)));
	colors[7] = rgbToGray(texture(iChannel0, uv + vec2(0, -stepY)));
	colors[8] = rgbToGray(texture(iChannel0, uv + vec2(stepX, -stepY)));

    // apply color steps to original color
    for(int i=0; i < 9; i++) {
    	colors[i] = floor(colors[i] * posterSteps) / posterSteps;
    }

    // count up colors in totals count array
	int colorsCount[9];
    for(int i=0; i < 9; i++) {
        colorsCount[i] = 0;
        for(int j=0; j < 9; j++) {
            if(colors[i] == colors[j]) colorsCount[i] += 1;
        }
    }

    // find most common color in kernel
    int maxColors = 0;
    int maxIndex = 0;
    for(int i=0; i < 9; i++) {
        if(colorsCount[i] > maxColors) {
            maxColors = colorsCount[i];
        	maxIndex = i;
        }
    }

    // draw most common color in kernel (or original)
    if(mod(iTime, 1.) > 0.5) {
    	fragColor = vec4(vec3(colors[4]), 1.0);
    } else {
    	fragColor = vec4(vec3(colors[maxIndex]), 1.0);
    }
}
