#define THRESHOLD 0.001

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    mat3 sobel[2];
    sobel[0] = mat3(
        -1,-2,-1,
         0, 0, 0,
         1, 2, 1
    );
    sobel[1] = mat3(
        -1, 0, 1,
        -2, 0, 2,
        -1, 0, 1
    );

    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 value = vec2(0.5*sin(iTime));

    for (int mat_id = 0; mat_id < 2; mat_id++) {
        for (int i = 0; i < 3; i++){
            for (int j = 0; j < 3; j++){
              vec2 offset = vec2(i - 1, j - 1) / iResolution.xy;
              float kernel = sobel[mat_id][i][j];
              vec4 color = texture(iChannel0, uv + offset);
              float luma = dot(color.rgb, vec3(0.299, 0.587, 0.114))*(1.0-0.5*sin(iTime));

              value[mat_id] += luma * kernel;
            }
        }
    }

    fragColor = step(THRESHOLD, length(value)) * vec4(vec3(length(value)), 1.0);

    // mix+step is faster than if-else
    //fragColor = mix(
    //    vec4(vec3(length(value)), 1.0), 
    //    vec4(0), 
    //    step(length(value), THRESHOLD));

}
