void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 mouse=iMouse.xy/iResolution.xy;
	float gscale=1.0;//iMouse.x/iResolution.x*2.0;
	float c = 0.0;
	const int passes = 10;
	float div = 0.0;
	for(int i=1;i<=passes;i++){
		float scale;
		if(uv.x<0.5)
			if(uv.y>0.5)
		 		scale = pow(2.0,float(i));
			else
				scale = pow(1.3,float(i));
		else
			if(uv.y>0.5)
		 		scale = float(i);
			else
				scale = 1.0;
		c+=texture(iChannel0,gscale*vec2((float(i)*(mouse.x + uv.x)),(float(i)*(mouse.y + uv.y)))).r/scale;
		div+=1.0/scale;
	}
	c/=div;
	if((uv.x>0.499 && uv.x<0.501) || (uv.y>0.497 && uv.y<0.503))
		fragColor = vec4(1);
	else
		fragColor = vec4(c);
}
