#define PI 3.1415926536
float df( vec2 p )
{
	float v = 1.0;
	const int iterations = 2;
	float zoom = 10. + iTime;
	float dist = distance( p, vec2( 0.5 ) );
	float angle = abs( atan( p.x - .5, p.y - .5 ) ) / PI ;
	// return angle;
	float audio = texture( iChannel0, vec2( 0, angle ) ).r; // Not full range!
	for( int i = 0; i < iterations; i++ ) {
		float progression = float( i ) / float( iterations );// * 2. * PI;
		v *= sin( zoom * ( dist + audio ) + angle * progression );// dist;
	}
	return v;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	float value = df( uv );
	fragColor.rgb = value * vec3(1.0,0.0,0.0);
	fragColor.a = 1.0;
}
