vec2 cinv( vec2 z ) { float d = dot(z,z);return vec2(z.x,-z.y)/d; }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = 1.75*(-iResolution.xy+2.0*fragCoord.xy)/iResolution.y;

	vec2 z = vec2(1.);
	float f = 4.;
	float g = 4.;


	for( int i=0; i<128; i++ ) 
	{
		float w = float(i)*.7+iTime*.5;
		vec2 z1 = 2.*vec2(cos(w),sin(w));
		z = cinv(z+p) - cinv(z-p) ;
		f = min( f, dot(z+z1,z+z1));
		g = min( g, dot(z-z1,z-z1));

	}

	f = 1.0+log(f)/15.0;
	g = 1.0+log(g)/15.0;

	fragColor = abs(vec4(g,f*.7,0.,1.0));
}
