vec3 rgb2hsv(vec3 c)
{
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

vec4 catSample(float time, vec2 uv)
{
    float frame = floor(mod(time * 14.0, 6.0));
    vec2 use_uv = uv * vec2(1.0/6.4, 1) + vec2(frame / 6.4, 0);
    return texture(iChannel0, use_uv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 screenUVs = fragCoord.xy / iResolution.xy;
    vec4 color = catSample(iTime, vec2(screenUVs.x, 1.0-screenUVs.y));

    vec3 diff_hsv = rgb2hsv(color.rgb);

    diff_hsv += vec3(iTime * 0.2,0,0);

	fragColor = vec4(hsv2rgb(diff_hsv),1);
}
