void mainImage( out vec4 fragColor, in vec2 fragCoord )
{  
    float xCirculo = fragCoord.x - iResolution.x * 0.5;
    float yCirculo = fragCoord.y - iResolution.y * 0.5;
    float funcionCirculo = xCirculo * xCirculo + yCirculo * yCirculo;
    float intensidad1 = sin(funcionCirculo * iTime * 0.003) * 1.0;
   	float intensidad2 = sin(funcionCirculo * iTime * 0.005 + 0.5) * 1.2;
    float intensidad3 = sin(funcionCirculo * iTime * 0.007 + 0.7) * 1.3;

    float r = sin(fragCoord.x / iResolution.x) * intensidad1;
    float g = sin(iTime + 0.2) * intensidad2;
    float b = sin(iTime + 0.5) * intensidad3;
	fragColor = vec4(

        r, 
        g, 
        b, 1.0);
}
