void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // using bufferA to distort a video
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 img = texture(iChannel0,uv);
    vec4 img2 = texture(iChannel2,uv);
    uv.x += img.r*0.2;
    vec4 yo = texture(iChannel1,uv);
    yo.r += img2.r*0.5;// mix in bufferB
    yo.g -= img2.b*0.5;
    fragColor = yo;
}

void mainImage( out vec4 O, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.y;
    float t = iTime * 1.7;
    t += fract(sin(uv.x+1.0))*2.0;
    float e = 1.0+sin(uv.x*3.0)*2.6;
    float k =  cos(t-e) + 7.0;
    O = vec4( abs( cos(t + sin(0.5 * t + (uv.x+t*0.001) * k) ) ));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    fragColor = texture(iChannel0, vec2(fract(iTime*0.01),(fragCoord.y / iResolution.y)));
}
