const float PI = 3.14159;
const float PERMITTIVITYOFFREESPACE = 8.85419 * pow(10.0, -12.0);
const float LIGHTSPEED = 2.99792 * pow(10.0, 8.0);


float DoubleSlitIntensity(float _E, float _n, float _p, float _b)
{
	float _I0; 
    _I0 = pow(_n, 2.0) * PERMITTIVITYOFFREESPACE * LIGHTSPEED * pow(_E, 2.0);
    return _I0 * 0.5 * pow(cos(_p / 2.0), 2.0) * pow(sin(_b / 2.0) / (_b / 2.0), 2.0);
}

float PhaseShift(float _d, float _R, float _l, float x)
{
	return (PI * _d * x) / (_l * _R);
}

float Beta(float _a, float _R, float _l, float x)
{
    return (PI * _a * x) / (_l * _R);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
   	vec2 uv = fragCoord;
    uv.x = uv.x - iResolution.x / 2.0;
    uv.y = uv.y - iResolution.y / 2.0; 

    vec4 _lightColor = vec4(0.0, 1.0, 0.0, 1.0);

    float _d = 0.000001;  					//Distance between slits in meters.
    float _a = 0.000000005;					//Width of slits in meters.
    float _n = 2.0;							//Number of slits.
    float _l = 510.0 * pow(10.0, -9.0);		//WaveLength (nm).
    float _R = 1.0;							//Distance between slits and screen.
    float _E = 10.0;						//Efield amplitude.

    float _b = Beta(_a, _R, _l, uv.y);			//Beta
    float _p = PhaseShift(_d, _R, _l, uv.y); 	//Phase shift
    float _I = DoubleSlitIntensity(_E, _n, _p, _b);	//Intensity


	fragColor = _lightColor * _I;
}
