void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.xy/iResolution.xy;
    vec3 color = vec3(0.0);

    float left = - smoothstep( cos(iTime) / 2.0, cos(iTime) / 2.0 + 0.7, st.x) + 1.0;
    float top = cos(st.x);
    float bottom = cos(st.y * 1000.0 * cos(iTime/2.0));

    color = top * bottom * left * vec3( cos(iTime)+st.x , cos(iTime)+ st.y, 1.0 ); 
    fragColor = vec4(color,1.0);

}
