void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 duv = vec2(1.0)/200.0;

    vec4 aa = texture(iChannel1, uv - duv);
    vec4 ab = texture(iChannel1, uv - vec2(duv.x, 0));
    vec4 ba = texture(iChannel1, uv - vec2(0, duv.y));
    vec4 bb = texture(iChannel1, uv);
    vec4 cc = texture(iChannel1, uv + duv);
    vec4 cb = texture(iChannel1, uv + vec2(duv.x, 0));
    vec4 bc = texture(iChannel1, uv + vec2(0, duv.y));
    vec4 ca = texture(iChannel1, uv + vec2(duv.x, -duv.y));
    vec4 ac = texture(iChannel1, uv + vec2(-duv.x, duv.y));

	vec4 lowest = bb;
    lowest = min(lowest, aa);
    lowest = min(lowest, ab);
    lowest = min(lowest, ba);
    lowest = min(lowest, cc);
    lowest = min(lowest, cb);
    lowest = min(lowest, bc);
    lowest = min(lowest, ca);
    lowest = min(lowest, ac);

    fragColor = (aa + ab + ba + cc + cb + bc + ca + ac + bb + lowest*4.)/13.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 duv = vec2(3.0, 3.0)/100.0;

    vec4 aa = texture(iChannel0, uv - duv);
    vec4 ab = texture(iChannel0, uv - vec2(duv.x, 0));
    vec4 ba = texture(iChannel0, uv - vec2(0, duv.y));
    vec4 bb = texture(iChannel0, uv);
    vec4 cc = texture(iChannel0, uv + duv);
    vec4 cb = texture(iChannel0, uv + vec2(duv.x, 0));
    vec4 bc = texture(iChannel0, uv + vec2(0, duv.y));
    vec4 ca = texture(iChannel0, uv + vec2(duv.x, -duv.y));
    vec4 ac = texture(iChannel0, uv + vec2(-duv.x, duv.y));

	vec4 lowest = bb;
    lowest = min(lowest, aa);
    lowest = min(lowest, ab);
    lowest = min(lowest, ba);
    lowest = min(lowest, cc);
    lowest = min(lowest, cb);
    lowest = min(lowest, bc);
    lowest = min(lowest, ca);
    lowest = min(lowest, ac);

    if (dot(lowest, lowest) > 2.5) {
	    fragColor = vec4(1.0);
    } else {
        fragColor = vec4(0.0);
    }
}
