void mainImage(out vec4 o, vec2 u) {
    u /= iResolution.x;
    float t = iTime, c = fract(t*.1-u.y), n=c;
    for(;n++<99.;)
        o += vec4(1, u, 1) * (length(cos(o.xyz - vec3(t,0,t))) - .3);
    u-=.5;
    o =   c<.3 ? max( u = fract(2.*u/dot(u,u)-t), u.x - u ).yyyy
        : c<.6 ? texture( iChannel0, fract(t*.2-vec2(u.x,1)/u.y ) )* -u.y*3.
        : o/50.;}

