void mainImage(out vec4 o,vec2 u)
{
    o-=o;
    float t = iTime, c = fract(t*.1);
    vec2 U = u/dot(u = u / iResolution.x - .5, u);

    for (int n=0; n<80; n++)
        o += vec4(1, u+.5, 1) * (length(cos(o.xyz + t)) - .3);
