void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv.x  *= iResolution.x / iResolution.y;

    fragColor = vec4(0.8,0.8,0.8,1.0);

    // circle define
    vec3 color = vec3(0.3,0.3,0.8); // blue
    //vec2 pos=vec2(0.2,0.5);
    vec2 circlepos=iMouse.xy / iResolution.xy;
    circlepos.x *= iResolution.x / iResolution.y;

    float radius=0.1;

    // circle area draw
    float len=length(uv-circlepos)-radius;
    len *= -1.0;
    len = clamp(len,0.0,1.0);
    if( len>0.0 )
    {
    	len = ceil(len);
    	color *= len;
		fragColor.xyz = color;
    }

}
