float noise(vec2 v)
{
    return texture(iChannel1, vec2(v), 4.0).x;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 nuv = uv * 2.0 - 1.0;

    float bpm = 1.0 / 60.0 * 110.0;
    float t = iTime;
    float bt = t * bpm;
    float pi = 3.14159265359;
    float btp = bt * pi;

    float ms = 1.0 / 32.0;
    float m0 = texture(iChannel0, vec2(0.0, 0.25)).x;
    float m1 = texture(iChannel0, vec2(ms * 14.0, 0.25)).x;
    float m2 = texture(iChannel0, vec2(ms * 28.0, 0.25)).x;

    float m0c = smoothstep(0.0, 1.0, pow(m0, 2.0) * 0.5);
    float m1c = smoothstep(0.0, 1.0, pow(max(0.0, m1 - 0.1), 2.0) * 2.0);
    float m2c = smoothstep(0.0, 1.0, pow(max(0.0, m2 - 0.2), 0.7) * 1.0);

    vec3 c = vec3(0.0);

    vec3 col0 = vec3(0.1);
    vec3 col1 = vec3(0.4);
    vec3 col2 = vec3(1.0);

    float side = step(0.35, uv.y);
    float iside = 1.0 - side;

    float shape = sin(length(nuv - vec2(0.0, 0.5)) * 20.0) * sin(length(nuv - vec2(sin(btp * 0.1) * 0.01, 0.3 + cos(btp * 0.1) * 0.1)) * 20.0);

    shape -= 1.0 - length(nuv) * 1.0;
    m0c *= abs(nuv.x);

    c += col2 * 0.2;
    c += col2 * pow(abs(sin(uv.y * 30.0 * uv.y + btp * 0.1)) * 0.6, 1.0) * 0.1;
    c += col2 * pow(abs(sin((uv.y - pow(0.33 * abs(nuv.x), 1.7 + abs(nuv.x))) * 70.0 * uv.y + btp * 0.105)) * 0.6, 1.0) * 0.2;
    c += noise(uv) * -shape * uv.y;

    c.xyz *= pow(length(c.xyz), 3.0 + m2 * 4.0);
    //c.xyz += pow(length(c.xyz), 12.2);

    vec3 d = vec3(0.0);

    d = col1;
    d += noise(uv) * shape;
    d += col2 * noise(nuv) * m0c * 1.5;
    d.z += shape;
    d.y += shape * texture(iChannel0, vec2(uv.x, 0.75)).x * 1.0;

    fragColor.xyz = mix(c, d, side);

    float mt0 = side * step(0.35 + 0.05 + abs(sin(uv.x * 20.0 + (1.0 - abs(nuv.x) * 0.2)) + cos(uv.x * 75.0) * 0.3) * 0.1, uv.y);
    float mt1 = iside * step(0.0 + 0.05 + abs(sin(uv.x * 20.0 + (1.0 - abs(nuv.x) * 0.2)) + cos(uv.x * 75.0) * 0.3) * 0.1, 0.35 - uv.y * 0.85);

    mt1 *= uv.y * 5.0 + noise(vec2(uv.x, uv.y) * 20.0) * 2.2;

    float mt = (1.0 - (mt0 + mt1));

    fragColor.xyz = mix(fragColor.xyz, vec3(0.0), mt);
}
