// Tuned via XShade (http://www.funparadigm.com/xshade/)

const float AA = 2.;
void mainImage( out vec4 f, in vec2 g )
{
    for( float m=0.; m<AA; m++ )
    f = vec4(0);

	vec2 si = iResolution.xy;

    for( float m=0.; m<AA; m++ )
    for( float n=0.; n<AA; n++ )
    {
        vec2 o = vec2(m,n) / AA - .5;
        vec2 uv = ((g+o)*2.-si)/min(si.x,si.y) * 20.;
     	float d = max(abs(uv.x),abs(uv.y)) + length(uv)*sin(iTime*.25); 
    	f += .5 + .5 * sin( 3. + (1. + fract(cos(d)*sin(d)+sin(iTime)*.5)  ) * 2. + vec4(1,0,1,1));
	}

    f /= AA * AA;
}
