bool wave(vec2 r, float box, float width, float wavewidth, float Time){
	if(mod(r.x + width / 2.0, box) <= width || mod(r.y + width / 2.0, box) <= width){
        float dis = abs(r.x) + abs(r.y);
        if(dis <= Time  / 1.0 + wavewidth && dis >= Time / 1.0){
    		return true;
        }
    }
    return false;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float GlobalTime = mod(iTime, 13.5);
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 r = 2.0 * vec2(uv.x - 0.5, uv.y - 0.5);
    r.x *= iResolution.x / iResolution.y;
    vec3 ret = vec3(0.1);
    vec3 col = vec3(0.5, 0.7, 1.0);
    float box = 0.5;
    float width = 0.05;
    float wavewidth = 0.05;
    if(mod(r.x + width / 2.0, box) <= width || mod(r.y + width / 2.0, box) <= width){
        float dis = abs(r.x) + abs(r.y);
        if(dis <= GlobalTime * 1.0  / 1.0 + wavewidth && dis >= GlobalTime * 1.0 / 1.0){
    		ret = col;
        }
    }
    vec2 r1 = r - vec2(0.8);
    if(wave(r1, box, width, wavewidth, (GlobalTime - 2.0) * 1.5))
        ret = col;

    vec2 r2 = r - vec2(-0.5);
    if(wave(r2, box, width, wavewidth, (GlobalTime - 3.0) * 1.0))
        ret = col;

    vec2 r3 = r - vec2(1.0, -0.3);
    if(wave(r3, box, width, wavewidth, (GlobalTime - 4.0) * 1.4))
        ret = col;

    vec2 r4 = r - vec2(-1.2, 0.8);
    if(wave(r4, box, width, wavewidth, (GlobalTime - 4.0) * 0.8))
        ret = col;

    vec2 r5 = r - vec2(0.2, -0.8);
    if(wave(r5, box, width, wavewidth, (GlobalTime - 6.0) * 1.2))
        ret = col;

    vec2 r6 = r - vec2(-1.0, 0.6);
    if(wave(r6, box, width, wavewidth, (GlobalTime - 7.0) * 1.1))
        ret = col;

    vec2 r7 = r;
    if(wave(r7, box / 1.2, width * 1.2, wavewidth * 1.5, (GlobalTime - 9.0) * 0.7))
        ret = col;

	fragColor = vec4(ret,1.0);
}
