void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float fudge = 10.0;
	float fudge2 = 0.5;
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec4 texColor = texture(iChannel0, uv);
	float texBW = distance(vec3(0.0, 0.0, 0.0), texColor.xyz);
	fragColor =  vec4(
		exp(dFdx(texBW)*fudge)*fudge2,exp(dFdy(texBW)*fudge)*fudge2,texBW, 1.0);
}
