const float PI = 3.14159265;

float time;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
    time = iTime *0.2;

	float xSin1_mult = 120.0;
	float xSin2_mult = 142.3;

	float ySin1_mult = 90.0;
	float ySin2_mult = 150.3;

	float xVal1 = sin(fragCoord.x / xSin1_mult + sin(time * 1.1) * 5.0);
	float xVal2 = sin(fragCoord.x / xSin2_mult + sin(time / 1.2) * 6.0);

	float yVal1 = sin(fragCoord.y / ySin1_mult + sin(time * 1.25) * 2.6);
	float yVal2 = sin(fragCoord.y / ySin2_mult + sin(time / 1.05) * 5.5);

	float red   = (xVal1 + xVal2 + yVal1 + yVal2 + 1.0) / 4.0;
	float green = (xVal1 + xVal2 + yVal1 + yVal2 + 1.0) / 3.0;
	float blue  = 0.0 - (xVal1 + xVal2 + yVal1 + yVal2 - 1.0) / 4.0;

	fragColor = vec4(red, green, blue, 0);
}
