vec4 rect(vec2 uv, vec2 pos, vec2 dim, vec3 col, vec3 bcol){

	if ((uv.x>=pos.x && uv.x<=pos.x+dim.x) && (uv.y>=pos.y && uv.y<=pos.y+dim.y)) {
		return vec4(col,1.0);
	}
	return vec4(bcol,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec3 bcol = vec3(0.5,1.0,0.2);
	fragColor = rect(uv,vec2(0.2,0.2),vec2(0.6,0.6),vec3(0.0,0.0,1.0),bcol);
}
