vec2 v=vec2(0,1);

float f(vec3 p)
{
    float t=iTime*3.;
    p.xy*=mat2(cos(t),-sin(t),sin(t),cos(t));
    t=iTime*sin(iTime*3E-3);
    p.zx*=mat2(cos(t),-sin(t),sin(t),cos(t));
    t=iTime*2.;
    p.yz*=mat2(cos(t),-sin(t),sin(t),cos(t));
    float r=.15;
    float d=length(p+v.yxx*.4)-r;
    d=min(d,length(p-v.yxx*.4)-r);
    d=min(d,length(p+v.xyx*.4)-r);
    d=min(d,length(p-v.xyx*.4)-r);
    d=min(d,length(p+v.xxy*.4)-r);
    d=min(d,length(p-v.xxy*.4)-r);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p=(fragCoord.xy*2.-iResolution.xy)/iResolution.x;
    vec3 r=normalize(v.yxx*p.x+v.xyx*p.y-v.xxy);

    float minL=1E-3,maxL=1E4,rL,rD=minL*2.;
    vec3 rP;
    for(int c=0;c<16;c++)
    {
        if(abs(rD)<minL||maxL<rL)break;
        rP=v.xxy+r*rL;
        rD=f(rP);
        rL+=rD;
    }

    float col;
    if(rD<1E-2)
    {
        col=iTime*9.-length(p)*3E2;
    }else{
        col=iTime*9.-length(p)*3E2+3.1415;
    }
    fragColor=vec4((.5+.5*vec3(sin(col),sin(col+1.),sin(col+2.))),1);
}
