void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy / iResolution.xy;

    float sinK = sin(iTime);
    float cosK = cos(iTime);

    fragColor =
        texture(iChannel0, xy * sinK)
      * vec4(
            abs(sinK)
          , abs(cosK)
          , abs(sinK * cosK)
          , 0.5);
}
