#define PI 3.14159
#define TWOPI 6.283185

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    uv = 2.0*(uv - 0.5);
    //uv.x *= iResolution.x/iResolution.y;

    float theta = mod(atan(uv.x, uv.y) + PI + 1.0, TWOPI);
    float r = length(uv);

    vec2 sampUv = vec2(theta/TWOPI, 1.0-r);

	fragColor = texture(iChannel0, sampUv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float fa = texture(iChannel0, vec2(uv.x, 0.5)).x;
    float fb = texture(iChannel0, vec2(uv.x, 0.25)).x;
    float fc = texture(iChannel0, vec2(uv.x, 0.75)).x;
    vec3 belowCol = texture(iChannel1, uv + vec2(0.0, 1.0/iResolution.y)).rgb;

    vec3 color = belowCol * 0.95;

    color += smoothstep(0.4, 0.6, (1.0-uv.y)*vec3(fa, fb, fc));

	fragColor = vec4(color,1.0);
}
