#define S(a, b, t) smoothstep(a, b, t)
#define sat(x) clamp(x, 0., 1.)

float remap01(float a, float b, float t)
{
    return sat((t-a)/(b-a));
}

float remap(float a, float b, float c, float d, float t)
{
    return sat((t-a)/(b-a) * (d-c)+c);
}

vec2 within(vec2 uv, vec4 rect)
{
    return (uv-rect.xy)/(rect.zw-rect.xy);
}

vec4 Eye (vec2 uv)   
{
    uv -= .5;

    float d = length(uv);


    vec4 irisCol =  vec4(.3, .5, 1., 1.);
    vec4 col = mix(vec4(1.), irisCol, S(.1, .7, d));

    col.rgb *= 1. -S(.45, .5, d)*.5*sat(-uv.y-uv.x); //sat makes sure the valu stays positive

    //eyeShadow *= remap(.41, .1, .75, 0., uv.x);
    //col.rgb = mix(col.rgb, eyeShadow.rgb, S(.3, .28, d));

    col.rgb = mix(col.rgb, vec3(0.), S(.3, .28, d));

    //irisCol.rgb *= mix(vec3(1.), irisCol.rgb, S(.1, .26, d));
    irisCol.rgb *= 1. + S(.3, .05, d);
    col.rgb = mix(col.rgb, irisCol.rgb, S(.28, .23, d));
    //col.rgb = mix(col.rgb, irisCol.rgb, S(.28, .23, d));

    irisCol *= vec4(0., 0., 0., 1.);
    col.rgb = mix(col.rgb, vec3(.0), S(.16, .14, d));


    float highlight = S(.1, .09, length(uv-vec2(-.15, .15)));
    col.rgb = mix(col.rgb, vec3(1.), highlight);

    col.a = S(.5, .48, d);
	return col;
}

vec4 Mouth (vec2 uv)   
{
    vec4 col = vec4(0.9, .65, .1, 1.);
	return col;
}

vec4 Head (vec2 uv)   
{
    vec4 col = vec4(0.9, .65, .1, 1.);
    float d = length(uv);

    col.a = S(.5, .49, d);
    float edgeShade = remap01(0.35, .5, d);

    edgeShade *= edgeShade;
    col.rgb *= 1.-edgeShade*.5;
    col.rgb = mix(col.rgb, vec3(.6, .3, .1), S(.47, .48, d));


    float hihlight = S(.41, .405, d) *.5;
    hihlight *= remap(.41, .1, .75, 0., uv.y);
    col.rgb = mix(col.rgb, vec3(1.), hihlight);

    d = length(uv-vec2(.25,-.14));
    float cheek = S(.2,.0, d);
    col.rgb = mix(col.rgb, vec3(.9,.4,.1), cheek);

    //d = length(uv-vec2(-.2,-.1));
    //cheek = S(.2,.0, d);           
    //col.rgb = mix(col.rgb, vec3(.9,.4,.1), cheek);

	return col;
}

vec4 Smiley (vec2 uv)   
{
    vec4 col = vec4(0.);

    uv.x = abs(uv.x);
    vec4 head = Head(uv);
    vec4 eye = Eye(within(uv, vec4(.03, -.1, .37, .25)));

    col = mix(col, head, head.a);
    col = mix(col, eye, eye.a);


	return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy; // uv.y 1 on top of screen pixel coordiant
    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;

    fragColor = Smiley(uv);
}
