void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float lineWidth = 0.02;
    vec3 c = vec3(uv, uv.y*uv.x);
    float amplitude = sin(iTime)*0.5;
    float y = sin(uv.x*9.0+iTime*1.5)*amplitude+0.5;
    if(uv.y > y-lineWidth && uv.y < y+lineWidth) {
        c = vec3(1.0, 1.0, 1.0);
    }
	fragColor = vec4(c, 1.0);
}
