void mainImage(out vec4 color, in vec2 pixCoords)
{
    vec2 posCenter = vec2 (0.5*iResolution.x, 0.5*iResolution.y);

    vec2 newCoords = pixCoords - posCenter;

    float rayon =  0.4*iResolution.y;

    float period = iTime / 5.;

    vec3 soleil = vec3(cos(period), 0., sin(period));

    vec4 lum0 = vec4(0.4, 0.6, 0.8, 1.0);

    vec3 vecteurCentrePoint = vec3(newCoords.x, newCoords.y, sqrt(pow(rayon, 2.) - pow(newCoords.x, 2.) - pow(newCoords.y, 2.)));

    vec3 haut = vecteurCentrePoint / rayon;
    float lumRatio = dot(haut, soleil);

	vec4 lum = lum0 *max(0.1,  (lumRatio));

	if (sqrt(pow(newCoords.x, 2.0) + pow(newCoords.y, 2.0)) < rayon)
    {
       	color = lum;
    }
    else
    {
        color = vec4(0.0);
    }  
}
