vec2 ratio = vec2(0.0);
vec2 uv = vec2(0.0);

#define balls 1000.0


float visualize_particle(float i){
    i /= iResolution.x;
    vec2 p = vec2(mod(i, iResolution.x), floor(i / iResolution.x));
    vec2 position = texture(iChannel0, p).rg;
	return 0.5 * (1.0 - smoothstep(0.0022, 0.00622, distance(position * ratio, uv * ratio )));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	uv = fragCoord.xy / iResolution.xy;
    ratio = vec2(1.0, iResolution.y / iResolution.x);
    float v = 0.0;
    for(float i=0.0;i<balls;i+=1.0){
     	v += visualize_particle(i);   
    }
	fragColor = vec4(vec3(v), 1.0);
}

vec2 uv = vec2(0.0);
vec2 mouse = vec2(0.0);

// iq value noise
float noise3d( in vec3 x ){
    #define hash(n) fract(sin(n)*758.5453)
	vec3 p = floor(x);
	vec3 f = fract(x);
	f = f*f*(3.0-2.0*f);
	float n = p.x + p.y*157.0 + 113.0*p.z;
	return mix(mix(	mix( hash(n+0.0), hash(n+1.0),f.x),
    mix(hash(n+157.0), hash(n+158.0),f.x),f.y),
    mix(mix( hash(n+113.0), hash(n+114.0),f.x),
    mix(hash(n+270.0), hash(n+271.0),f.x),f.y),f.z);
}

vec4 update(){
    // aquire backbuffer particle data
	vec2 position = texture(iChannel0, uv).rg;
	vec2 velocity = texture(iChannel0, uv).ba;	

    vec2 n = normalize((mouse) - position);
    float D = distance(mouse, position);

    // calculate mouse "drag"
    velocity +=  0.25 * n * (1.0 / (D * D*10.0 + 1.0));

    // update particle position
    position += velocity * 0.0256;

    // branchless collision box
    velocity.y *= max(-0.95, sign(position.y));
    velocity.y *= mix(1.0, -0.8, step(1.0, position.y));
    velocity.x *= max(-0.95, sign(position.x));		
    velocity.x *= mix(1.0, -0.8, step(1.0, position.x));

    // air drag
    velocity *= 0.97;

    // random "wind"
	position += (vec2(noise3d(vec3(uv.x * 43.4, uv.y * 43.4, iTime)), 
                     noise3d(vec3(-uv.x * 43.4, -uv.y * 43.4, -iTime))) * 2.0 - 1.0) * 0.02;

    //gravity
    velocity += vec2(0.0, -0.052);

    return vec4(clamp(position, 0.01, 0.99), clamp(velocity, -1.0, 1.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    uv = fragCoord.xy / iResolution.xy;
    mouse = iMouse.xy / iResolution.xy;
    fragColor = update();
}
